import { useState, useCallback, useMemo } from "react";
import {
  ChakraProvider,
  Button,
  Box,
  Text,
  Link,
  VStack,
  Code,
  Grid,
  GridItem,
  Editable,
  EditableInput,
  EditableTextarea,
  EditablePreview,
  theme,
  Center,
  Container,
  Image,
  HStack,
  Flex,
  ButtonGroup,
  IconButton
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';
import { Logo } from './Logo';
import { FiMenu } from 'react-icons/fi'

import axios from "axios";
import { useDropzone } from "react-dropzone";

const baseStyle = {
  // display: "flex",
  // flexDirection: "column",
  // width: 200,
  // h: 100,
  // margin: 10
  height: '80%',
  border: '3px',
  borderColor: 'gray'
};

const borderNormalStyle = {
  borderStyle: 'dotted',
  borderColor: 'gray'
};
const borderDragStyle = {
  border: "1px solid #00f",
  transition: "border .5s ease-in-out"
};

function App() {
  const [fileUrl, setFileUrl] = useState();
  const [ocrText, setOcrText] = useState();

  const onDrop = useCallback((acceptedFiles) => {
    // Do something with the files
    console.log("acceptedFiles:", acceptedFiles);
    if (acceptedFiles.length > 0) {
      const src = URL.createObjectURL(acceptedFiles[0]);
      setFileUrl(src);
      // setUploadFile(acceptedFiles[0]);
    }
  }, []);

  const {
    getRootProps,
    getInputProps,
    isDragActive,
    open,
    acceptedFiles
  } = useDropzone({
    onDrop,
    noClick: true
  });

  const style = useMemo(
    () => ({
      ...baseStyle,
      ...(isDragActive ? borderDragStyle : borderNormalStyle)
    }),
    [isDragActive]
  );

  const postAvator = async () => {
    const url = "http://localhost:9004/convert/";
    const data = new FormData();
    data.append('file', acceptedFiles[0]);
    const headers = { "content-type": "multipart/form-data" };
    axios
      .post( url, data, { headers })
      .then((res) => {
        console.log(res.data.ocrresult);
        setOcrText(res.data.ocrresult);
      })
      .catch(err =>{
        console.log(err);
      } );
    }




  return (
    <ChakraProvider theme={theme}>
      <Box
      as="section"
      pb={{
        base: '12',
        md: '24',
      }}
    >
      <Box as="nav" bg="bg-surface" boxShadow="sm">
        <Container
          py={{
            base: '4',
            lg: '5',
          }}
        >
          <HStack spacing="10" justify="space-between">
            <Logo />
            {isDesktop ? (
              <Flex justify="space-between" flex="1">
                <ButtonGroup variant="link" spacing="8">
                  {['Product', 'Pricing', 'Resources', 'Support'].map((item) => (
                    <Button key={item}>{item}</Button>
                  ))}
                </ButtonGroup>
                <HStack spacing="3">
                  <Button variant="ghost">Sign in</Button>
                  <Button variant="primary">Sign up</Button>
                </HStack>
              </Flex>
            ) : (
              <IconButton
                variant="ghost"
                icon={<FiMenu fontSize="1.25rem" />}
                aria-label="Open Menu"
              />
            )}
          </HStack>
        </Container>
      </Box>
    </Box>
      <Grid
        h='600px'
        templateRows='repeat(3, 1fr)'
        templateColumns='repeat(7, 1fr)'
        gap={4}
      >
        <GridItem rowSpan={1} colSpan={7} bg="tomato">
          <Center h='100%'>
            <Container {...getRootProps({ style })}>
              <Center>
                <input {...getInputProps()} />
                {isDragActive ? (
                  <p>Drop the files here ...</p>
                ) : (
                  <p>Drag 'n' drop some files here, or click to select files</p>
                )}
              </Center>
              <Center>
                <Button
                  type="button"
                  colorScheme="green"
                  onClick={open}
                  className="btn btn-primary align-self-center"
                >
                  Select files
                </Button>
              </Center>
            </Container>
          </Center>
        </GridItem>

        <GridItem rowSpan={2} colSpan={3} bg='papayawhip'>
          <Center h='100%'>
            <div className="card mt-2" style={{ margin: 'auto', width: '200px', height: '200px' }} >
              <Image src={fileUrl} alt="" width="100%" />
            </div>
          </Center>
        </GridItem>

        <GridItem rowSpan={2} colSpan={1} bg='papayawhip'>
          <Center h='100%'>
            <Button
              type="button"
              colorScheme="green"
              onClick={postAvator}
              className="btn btn-primary align-self-center"
              >
              読み取り開始
            </Button>
          </Center>
        </GridItem>

        <GridItem rowSpan={2} colSpan={3} bg='papayawhip'>
          <Editable value={ocrText} h='100%' whiteSpace='pre-wrap'>
            <EditablePreview h='100%'/>
            <EditableTextarea h='100%'/>
          </Editable>
        </GridItem>
      </Grid>
    </ChakraProvider>
  );
}

export default App;
