import { useState, useCallback, useMemo } from "react";
// import React from 'react';
import {
  ChakraProvider,
  Button,
  Box,
  Text,
  Link,
  VStack,
  Code,
  Grid,
  GridItem,
  Editable,
  EditableInput,
  EditableTextarea,
  EditablePreview,
  theme,
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';
import { Logo } from './Logo';

import axios from "axios";
import { useDropzone } from "react-dropzone";

const baseStyle = {
  display: "flex",
  flexDirection: "column",
  width: 200,
  height: 150,
  margin: 10
};

const borderNormalStyle = {
  border: "1px dotted #888"
};
const borderDragStyle = {
  border: "1px solid #00f",
  transition: "border .5s ease-in-out"
};

function App() {
  const [fileUrl, setFileUrl] = useState();
  const [ocrText, setOcrText] = useState();

  const onDrop = useCallback((acceptedFiles) => {
    // Do something with the files
    console.log("acceptedFiles:", acceptedFiles);
    if (acceptedFiles.length > 0) {
      const src = URL.createObjectURL(acceptedFiles[0]);
      setFileUrl(src);
      // setUploadFile(acceptedFiles[0]);
    }
  }, []);

  const {
    getRootProps,
    getInputProps,
    isDragActive,
    open,
    acceptedFiles
  } = useDropzone({
    onDrop,
    noClick: true
  });

  const style = useMemo(
    () => ({
      ...baseStyle,
      ...(isDragActive ? borderDragStyle : borderNormalStyle)
    }),
    [isDragActive]
  );

  const files = useMemo(
    () =>
      acceptedFiles.map((file) => (
        <li key={file.path}>
          {file.path} - {file.size} bytes
        </li>
      )),
    [acceptedFiles]
  );

  const postAvator = async () => {
    const url = "http://localhost:9004/convert/";
    const data = new FormData();
    data.append('file', acceptedFiles[0]);
    const headers = { "content-type": "multipart/form-data" };
    axios
      .post( url, data, { headers })
      .then((res) => {
        console.log(res.data.ocrresult);
        setOcrText(res.data.ocrresult);
      })
      .catch(err =>{
        console.log(err);
      } );
    }




  return (
    <ChakraProvider theme={theme}>
      <Grid
        h='600px'
        templateRows='repeat(2, 1fr)'
        templateColumns='repeat(3, 1fr)'
        gap={4}
      >
        <GridItem rowSpan={1} colSpan={3} bg='tomato'>
          <div {...getRootProps({ style })}>
            <input {...getInputProps()} />
            {isDragActive ? (
              <p>Drop the files here ...</p>
            ) : (
              <p>Drag 'n' drop some files here, or click to select files</p>
            )}
            <Button
              type="button"
              colorScheme="green"
              onClick={open}
              className="btn btn-primary align-self-center"
            >
              Select files
            </Button>
          </div>
        </GridItem>
        <GridItem rowSpan={1} colSpan={1} bg='papayawhip' />
        <GridItem rowSpan={1} colSpan={1} bg='papayawhip'>
          <Button
            type="button"
            colorScheme="green"
            onClick={postAvator}
            className="btn btn-primary align-self-center"
          >
            OCR Read
          </Button>
        </GridItem>

        <GridItem rowSpan={1} colSpan={1} bg='papayawhip'>
          <Editable value={ocrText}>
            <EditablePreview />
            <EditableTextarea h='100%' />
          </Editable>
        </GridItem>
      </Grid>
      <div className="container">

        <aside>
          <h4>Files</h4>
          <ul>{files}</ul>
        </aside>
        <div className="card mt-2" style={{ margin: 'auto', width: '200px', height: '200px' }} >
          <img src={fileUrl} alt="" width="100%" />
        </div>


      </div>
    </ChakraProvider>
  );
}

export default App;
