import { useState, useCallback, useMemo } from "react";
// import React from 'react';
import {
  ChakraProvider,
  Box,
  Text,
  Link,
  VStack,
  Code,
  Grid,
  theme,
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';
import { Logo } from './Logo';

import axios from "axios";
import { useDropzone } from "react-dropzone";

function App() {
  const [fileUrl, setFileUrl] = useState();
  const [ocrText, setOcrText] = useState();

  const onDrop = useCallback((acceptedFiles) => {
    // Do something with the files
    console.log("acceptedFiles:", acceptedFiles);
    if (acceptedFiles.length > 0) {
      const src = URL.createObjectURL(acceptedFiles[0]);
      setFileUrl(src);
      // setUploadFile(acceptedFiles[0]);
    }
  }, []);

  const {
    getRootProps,
    getInputProps,
    isDragActive,
    open,
    acceptedFiles
  } = useDropzone({
    onDrop,
    noClick: true
  });

  const style = useMemo(
    () => ({
      ...baseStyle,
      ...(isDragActive ? borderDragStyle : borderNormalStyle)
    }),
    [isDragActive]
  );

  const files = useMemo(
    () =>
      acceptedFiles.map((file) => (
        <li key={file.path}>
          {file.path} - {file.size} bytes
        </li>
      )),
    [acceptedFiles]
  );

  const postAvator = async () => {
    const url = "http://localhost:9004/convert/";
    const data = new FormData();
    data.append('file', acceptedFiles[0]);
    const headers = { "content-type": "multipart/form-data" };
    axios
      .post( url, data, { headers })
      .then((res) => {
        console.log(res.data.ocrresult);
        setOcrText(res.data.ocrresult);
      })
      .catch(err =>{
        console.log(err);
      } );
    }




  return (
    <ChakraProvider theme={theme}>
      <Box textAlign="center" fontSize="xl">
        <Grid minH="100vh" p={3}>
          <ColorModeSwitcher justifySelf="flex-end" />
          <VStack spacing={8}>
            <Logo h="40vmin" pointerEvents="none" />
            <Text>
              Edit <Code fontSize="xl">src/App.js</Code> and save to reload.
            </Text>
            <Link
              color="teal.500"
              href="https://chakra-ui.com"
              fontSize="2xl"
              target="_blank"
              rel="noopener noreferrer"
            >
              Learn Chakra
            </Link>
          </VStack>
        </Grid>
      </Box>
    </ChakraProvider>
  );
}

export default App;
